import React, { useState } from "react";
import { Form, Input, Button, message } from "antd";
import axios from "axios";
//import "antd/dist/antd.css";
import { UserOutlined } from "@ant-design/icons";

const App = () => {
  const [form] = Form.useForm();

  const onFinish = async (values) => {
    try {
      const response = await axios.post("http://localhost:5007/signup", values);
      if (response.data.success) {
        message.success(response.data.message);
        // Navigate to login or another page
      } else {
        message.error(response.data.message);
      }
    } catch (error) {
      //console.error(error);

      message.error("An error occurred while registering the user");
    }
  };

  return (
    <div style={{ width: 300, margin: "100px auto" }}>
      <Form form={form} onFinish={onFinish}>
        <Form.Item
          name="fullName"
          rules={[{ required: true, message: "Please input your full name!" }]}
        >
          <Input placeholder="Full Name" prefix={<UserOutlined />} />
        </Form.Item>
        <Form.Item
          name="email"
          rules={[{ required: true, message: "Please input your email!" }]}
        >
          <Input placeholder="Email ID" />
        </Form.Item>
        <Form.Item
          name="password"
          rules={[{ required: true, message: "Please input your password!" }]}
        >
          <Input.Password placeholder="Create a Password" />
        </Form.Item>
        <Form.Item
          name="confirmPassword"
          dependencies={["password"]}
          hasFeedback
          rules={[
            { required: true, message: "Please confirm your password!" },
            ({ getFieldValue }) => ({
              validator(_, value) {
                if (!value || getFieldValue("password") === value) {
                  return Promise.resolve();
                }
                return Promise.reject(
                  new Error("The two passwords do not match!")
                );
              },
            }),
          ]}
        >
          <Input.Password placeholder="Confirm Password" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Signup
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default App;
